#include "TitleScreen.h"
#include "Colors.h"

#include "StdWilUtil.h"

TitleScreen::TitleScreen( const string &titleText ) :
    Sprite( RectF( Vector2f( 0, 0 ), sdl->getWindowSize() ) ) {
      
  // The texture for this is generated by sdl
  SDL_Texture *tex = sdl->makeTextTexture( titleText, White );
  
  addAnimationFrame( tex, White, HUGEF );
  
}

void TitleScreen::update() {
  // Use a random color
  Animation::Frame &af = animation.getCurrentFrame();
  af.color = SDL_RandomSolidColor();
  SDL_SetTextureColorMod( af.tex, af.color.r, af.color.g, af.color.b );
}
